import React from 'react';
import axios from 'axios';

export default function Your_application_form() {

  const handlePreviewClick = async () => {

    const token = localStorage.getItem('token'); 
    console.log('Retrieved token:', token);// Get the token from local storage
    if (!token) {
      console.error('No access token found.');
      return; // Early return if no token
  }

    try {
      const response = await axios.get('http://localhost:3000/api/PDF/genpdf',
       {
        headers: {
          Authorization: `Bearer ${token}`, // Send token in Authorization header
          'Content-Type': 'application/json',
      },
        responseType: 'blob', // Ensure the response is treated as a blob for PDF
      });


      if (response.status === 200) {
        const url = window.URL.createObjectURL(new Blob([response.data]));
        const link = document.createElement('a');
        link.href = url;
        link.setAttribute('download', 'YourApplication.pdf'); // Set the filename for the PDF
        document.body.appendChild(link);
        link.click();
        link.remove(); // Clean up
      } else {
        console.error('Failed to fetch profile data');
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };
  const handlePaymentClick = async () => {
    try {
      const amount = 500; // Example amount (in INR)

      // Step 1: Create order on the server
      const response = await axios.post('http://localhost:3000/api/payments/order', { amount });
      const { id: order_id, amount: order_amount, currency } = response.data;

      // Step 2: Initialize Razorpay
      const options = {
        key: 'YOUR_RAZORPAY_KEY_ID', // Replace with your Razorpay Key ID
        amount: order_amount,
        currency: currency,
        name: 'Your Application',
        description: 'Application Payment',
        order_id: order_id,
        handler: function (response) {
          console.log(response);
          alert('Payment successful!');
          // Add additional actions here (e.g., update database, show confirmation page)
        },
        prefill: {
          name: 'Your Name', // Replace with dynamic user name
          email: 'user@example.com', // Replace with dynamic user email
          contact: '9999999999', // Replace with dynamic user contact
        },
        theme: {
          color: '#3399cc',
        },
      };

      const razorpay = new window.Razorpay(options);
      razorpay.open();
    } catch (error) {
      console.error('Error:', error);
      alert('Failed to initiate payment.');
    }
  };

  return (
    <div>
      <p>Here you can preview your application and download it in pdf form</p>
      
      <div style={{ display: 'flex', gap: '10px', justifyContent: 'center' }}>
        <button onClick={handlePreviewClick} style={{
          backgroundColor: '#f44336',
          color: 'white',
          padding: '10px 20px',
          border: 'none',
          borderRadius: '5px',
          cursor: 'pointer',
          width: '118px',
        }}>
          Preview
        </button>
        
        <button onClick={handlePaymentClick} style={{
          backgroundColor: '#2196F3',
          color: 'white',
          padding: '10px 20px',
          border: 'none',
          borderRadius: '5px',
          cursor: 'pointer',
          width: '118px',
        }}>
          Payment
        </button>
      </div>
    </div>
  );
}
