import React, { useState } from 'react';
import '../../cssss/Student_css/Your_Profile.css';
import '../../cssss/Student_css/ChangePassword.css';

export default function YourApplicationForm() {
    const [currentPassword, setCurrentPassword] = useState('');
    const [newPassword, setNewPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [message, setMessage] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (newPassword !== confirmPassword) {
            setMessage("New passwords don't match.");
            return;
        }

        try {
            const response = await fetch('/api/change-password', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ currentPassword, newPassword }),
            });

            if (response.ok) {
                setMessage('Password successfully updated.');
            } else {
                const errorData = await response.json();
                setMessage(errorData.message || 'Failed to update password.');
            }
        } catch (error) {
            setMessage('An error occurred. Please try again.');
        }
    };

    return (
        <div className="dashboard-container change-password-container">
          {/* <div className='change-password-container'> */}
            <h2>Change Password</h2>
            <form onSubmit={handleSubmit}>
                {/* <label>
                    Current Password:
                    <input
                        type="password"
                        value={currentPassword}
                        onChange={(e) => setCurrentPassword(e.target.value)}
                        required
                    />
                </label> */}
                <label>
                    New Password:
                    <input
                        type="password"
                        value={newPassword}
                        onChange={(e) => setNewPassword(e.target.value)}
                        required
                    />
                </label>
                <label>
                    Confirm New Password:
                    <input
                        type="password"
                        value={confirmPassword}
                        onChange={(e) => setConfirmPassword(e.target.value)}
                        required
                    />
                </label>
                <button type="submit">Update Password</button>
            </form>
            {message && <p>{message}</p>}
            </div>
    );
}
