import React, { useState } from 'react';
import axios from 'axios';

export default function StudentRegistered() {
  const [selectedCourse, setSelectedCourse] = useState('');
  const [students, setStudents] = useState([]);
  const [error, setError] = useState(null);

  const handleCourseChange = (event) => {
    const course = event.target.value;
    setSelectedCourse(course);
    if (course) {
      fetchStudents(course);
    } else {
      setStudents([]); // Clear the table if no course is selected
    }
  };

  // Fetch students data from backend based on the selected course
  const fetchStudents = async (course) => {
    const adminToken = localStorage.getItem('adminToken'); // Retrieve the token from local storage
    console.log("Token from localStorage:", adminToken);

    try {
      const response = await axios.get(`http://localhost:3000/api/StuReg/studentsRegistered`, {
        params: { course },
        headers: {
          Authorization: `Bearer ${adminToken}`, // Attach token in Authorization header
        },
      });
      setStudents(response.data);
      setError(null); // Clear any previous error
    } catch (error) {
      console.error('Error fetching student data:', error);
      setError('Could not fetch students. Please try again later.');
    }
  };

  return (
    <div>
      <label htmlFor="course">Select your course:</label>
      <select
        id="course"
        value={selectedCourse}
        onChange={handleCourseChange}
      >
        <option value="">-- Select Course --</option>
        <option value="Btech">Btech</option>
        <option value="Mca">MCA</option>
        <option value="BE">BE</option>
        <option value="Mba">MBA</option>
        <option value="Bca">BCA</option>
      </select>

      {selectedCourse && (
        <div>
          <p>You have selected: {selectedCourse}</p>
          {error ? (
            <p style={{ color: 'red' }}>{error}</p>
          ) : students.length > 0 ? (
            <table>
              <thead>
                <tr>
                  <th>Name</th>
                  <th>Age</th>
                  <th>Email</th>
                </tr>
              </thead>
              <tbody>
                {students.map((student) => (
                  <tr key={student._id}>
                    <td>{student.basicDetails.name}</td>
                    <td>{student.basicDetails.age}</td>
                    <td>{student.basicDetails.email}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          ) : (
            <p>No students found for this course.</p>
          )}
        </div>
      )}
    </div>
  );
}
