import React, { useState } from 'react';
import '../../cssss/Student_css/Your_Profile.css';
import Basic_Details from './Basic_Details';

export default function ApplicationForm (){

    /*const [districts, setDistricts] = useState([]);

    // Districts data for each state
    const districtsData = {
        Haryana: ['Palwal', 'Faridabad', 'Gurgaon', 'Hisar', 'Sonipat', 'Rohtak'],
        Punjab: ['Amritsar', 'Ludhiana', 'Jalandhar', 'Patiala', 'Mohali'],
        // Add more states and their districts here
        // ...
    };
    
    const handleChangedis = (e) => {
        const { name, value } = e.target;
        setFormData((prevData) => ({
            ...prevData,
            [name]: value,
        }));

        if (name === 'state') {
            setDistricts(districtsData[value] || []);
            setFormData((prevData) => ({
                ...prevData,
                district: '', // Reset district when state changes
            }));
        }
    };


    const [formData, setFormData] = useState({
        name: '',
        fatherName: '',
        motherName: '',
        session:'',
        mobileNo: '',
        email: '',
        aadharNo:'',
        familyId:'',
        photo: null,
        signature: null,
        xMarks: '',
        xMarksheet: null,
        xiiMarks: '',
        xiiMarksheet: null,
        state: '',
        district:'',
        city: '',
        pincode:'',
        address: '',
        programType: '',
        programApplied: '',
        dob: '',
        nationality: '',
        gender: '',
        maritalStatus: '',
        religion: '',
        category: '',
        subCategory: '',
        caste: '',
        fatherOccupation: '',
        familyIncome: '',
        fatherContact: '',
    });

    const handleChange = (e) => {
        const { name, value, files } = e.target;
        setFormData({
            ...formData,
            [name]: files ? files[0] : value,
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log(formData);
    };*/

    return (
      <>
        <Basic_Details/>
        {/* Address Section */}
        <div className="dashboard-container">
            <h2>Address</h2>
            <h6>Permanent Address:-</h6>
            <form onSubmit={handleSubmit}>
                <div className="form-fields-left">
                    <label htmlFor="state">State:</label>
                    <select
                        name="state"
                        value={formData.state}
                        onChange={handleChange}
                        required
                    >
                    <option value="">Select State</option>
                    <option value="Andaman and Nicobar Islands">Andaman and Nicobar Islands</option>
                    <option value="Andhra Pradesh">Andhra Pradesh</option>
                    <option value="Himachal Pradesh">Himachal Pradesh</option>
                    
                    </select>

                    <label htmlFor="city">City:</label>
                    <input
                        type="text"
                        id="city"
                        name="city"
                        value={formData.city}
                        onChange={handleChange}
                    />
                    <label htmlFor="address">Address:</label>
                    <textarea
                    name="address"
                    rows="4"
                    cols="50"
                    style={{
                        resize: 'both', // Allows both vertical and horizontal resizing
                        overflow: 'auto' // Adds scroll bars if text overflows
                    }}
                    placeholder="Enter your address here..."
                    >
                    </textarea>  
         
                </div>
                <div className="form-fields-right">
                    <label htmlFor="district">District:</label>
                    <select
                name="district"
                value={formData.district}
                onChange={handleChangedis}
                required
                disabled={!formData.state} // Disable if no state is selected
                >
                <option value="">Select District</option>
                {districts.map((district) => (
                    <option  value={district}>
                        {district}
                    </option>
                ))}
                </select>
                <label htmlFor="pincode">Pincode:</label>
                    <input
                        type="text"
                        id="pincode"
                        name="pincode"
                        value={formData.pincode}
                        onChange={handleChange}
                    />

                </div>

               
            </form> 
            <h6>Correspondence Address:-</h6>
            <label>
         <input
          type="checkbox"
        //   checked={checked}
          onChange={handleChange}
         />
         If Correspondence address is same as Permanent address
         </label>
            <form onSubmit={handleSubmit}>
                <div className="form-fields-left">
                    <label htmlFor="state">State:</label>
                    <select
                        name="state"
                        value={formData.state}
                        onChange={handleChange}
                        required
                    >
                    <option value="">Select State</option>
                    <option value="Andaman and Nicobar Islands">Andaman and Nicobar Islands</option>
                    <option value="Andhra Pradesh">Andhra Pradesh</option>
                    <option value="Himachal Pradesh">Himachal Pradesh</option>
                    
                    </select>

                    <label htmlFor="city">City:</label>
                    <input
                        type="text"
                        id="city"
                        name="city"
                        value={formData.city}
                        onChange={handleChange}
                    />
                    <label htmlFor="address">Address:</label>
                    <textarea
                    name="address"
                    rows="4"
                    cols="50"
                    style={{
                        resize: 'both', // Allows both vertical and horizontal resizing
                        overflow: 'auto' // Adds scroll bars if text overflows
                    }}
                    placeholder="Enter your address here..."
                    >
                    </textarea>  
         
                </div>
                <div className="form-fields-right">
                    <label htmlFor="district">District:</label>
                    <select
                name="district"
                value={formData.district}
                onChange={handleChangedis}
                required
                disabled={!formData.state} // Disable if no state is selected
                >
                <option value="">Select District</option>
                {districts.map((district) => (
                    <option  value={district}>
                        {district}
                    </option>
                ))}
                </select>
                <label htmlFor="pincode">Pincode:</label>
                    <input
                        type="text"
                        id="pincode"
                        name="pincode"
                        value={formData.pincode}
                        onChange={handleChange}
                    />

                </div>

               
            </form>       
        </div>

        {/* Educational Details Section */}
        {/* <div className="dashboard-container">
            <h2>Educational Details</h2>
            <form onSubmit={handleSubmit}>
                <div className="form-fields">
                    <label htmlFor="xMarks">Xth Marks:</label>
                    <input
                        type="text"
                        id="xMarks"
                        name="xMarks"
                        value={formData.xMarks}
                        onChange={handleChange}
                        required
                    />
                    <label htmlFor="xMarksheet">Upload Xth Marksheet:</label>
                    <input
                        type="file"
                        id="xMarksheet"
                        name="xMarksheet"
                        accept="image/*"
                        onChange={handleChange}
                        required
                    />

                    <label htmlFor="xiiMarks">XII Marks:</label>
                    <input
                        type="text"
                        id="xiiMarks"
                        name="xiiMarks"
                        value={formData.xiiMarks}
                        onChange={handleChange}
                        required
                    />
                    <label htmlFor="xiiMarksheet">Upload XII Marksheet:</label>
                    <input
                        type="file"
                        id="xiiMarksheet"
                        name="xiiMarksheet"
                        accept="image/*"
                        onChange={handleChange}
                        required
                    />
                </div>
            </form>
        </div> */}
      </>
    );
};
