import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../../cssss/Home_css/Rightside.css';


const RegistrationForm = () => {
  const navigate = useNavigate();
    const [isLogin, setIsLogin] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    mobile: '',
    otp: '',
    state: '',
    city: '',
    programType: '',
    programApplied: '',
    qualifyingExam: '',
    captcha: ''
  });

  const [loginData, setLoginData] = useState({
    email: '',
    password: ''
  });

  const [errors, setErrors] = useState({});
  //for registrationn form
  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleLoginChange = (e) => {
    setLoginData({
      ...loginData,
      [e.target.name]: e.target.value
    });
  };
  const handleSubmit = (e) => {
    e.preventDefault();
    // Simple form validation
    if (validateForm()) {
      console.log('Registration Form data submitted:', formData);
      // Call an API or handle the form submission logic here
      navigate('/student');
    }
  };
   // Handle login form submit
   const handleLoginSubmit = (e) => {
    e.preventDefault();
    if (validateLoginForm()) {
      console.log('Login form data submitted:', loginData);
      navigate('/student');
    }
  };

  const validateForm = () => {
    let formErrors = {};
    let isValid = true;

    if (!formData.name) {
      isValid = false;
      formErrors['name'] = 'Please enter your name';
    }

    if (!formData.email) {
      isValid = false;
      formErrors['email'] = 'Please enter your email address';
    }

    if (!formData.mobile) {
      isValid = false;
      formErrors['mobile'] = 'Please enter your mobile number';
    }

    // Other validations can go here

    setErrors(formErrors);
    return isValid;
  };
  const validateLoginForm = () => {
    let formErrors = {};
    let isValid = true;

    if (!loginData.email) {
      isValid = false;
      formErrors['email'] = 'Please enter your email';
    }

    if (!loginData.password) {
      isValid = false;
      formErrors['password'] = 'Please enter your password';
    }

    setErrors(formErrors);
    return isValid;
  };

  return (
    <div className="form-container">
    <div className="toggle-buttons">
      <button onClick={() => setIsLogin(false)} className={!isLogin ? 'active' : ''}>
        Register
      </button>
      <button onClick={() => setIsLogin(true)} className={isLogin ? 'active' : ''}>
        Login
      </button>
    </div>
    {/* Render the registration form */}
    {!isLogin ? ( 
    <form onSubmit={handleSubmit} className="registration-form">
      <h2>Register</h2>
      <div>
        <label>Enter Name *</label>
        <input
          type="text"
          name="name"
          value={formData.name}
          onChange={handleChange}
        />
        <span className="error">{errors.name}</span>
      </div>

      <div>
        <label>Enter Email Address *</label>
        <input
          type="email"
          name="email"
          value={formData.email}
          onChange={handleChange}
        />
        <span className="error">{errors.email}</span>
      </div>

      <div>
        <label>Enter Mobile Number *</label>
        <input
          type="tel"
          name="mobile"
          value={formData.mobile}
          onChange={handleChange}
        />
        <span className="error">{errors.mobile}</span>
      </div>

      <div>
        <label>Enter OTP</label>
        <input
          type="text"
          name="otp"
          value={formData.otp}
          onChange={handleChange}
        />
      </div>

      {/* <div>
        <label>Select State *</label>
        <select
          name="state"
          value={formData.state}
          onChange={handleChange}
        >
          <option value="">Select State</option>
          <option value="State1">State 1</option>
          <option value="State2">State 2</option>
        </select>
      </div>

      <div>
        <label>Select City *</label>
        <select
          name="city"
          value={formData.city}
          onChange={handleChange}
        >
          <option value="">Select City</option>
          <option value="City1">City 1</option>
          <option value="City2">City 2</option>
        </select>
      </div>

      <div>
        <label>Programme Type *</label>
        <select
          name="programType"
          value={formData.programType}
          onChange={handleChange}
        >
          <option value="">Select Program Type</option>
          <option value="Type1">Type 1</option>
          <option value="Type2">Type 2</option>
        </select>
      </div>

      <div>
        <label>Programme Applied For *</label>
        <select
          name="programApplied"
          value={formData.programApplied}
          onChange={handleChange}
        >
          <option value="">Select Program</option>
          <option value="Program1">Program 1</option>
          <option value="Program2">Program 2</option>
        </select>
      </div> */}
{/* 
      <div>
        <label>Select Qualifying Exam *</label>
        <select
          name="qualifyingExam"
          value={formData.qualifyingExam}
          onChange={handleChange}
        >
          <option value="">Select Exam</option>
          <option value="Exam1">Exam 1</option>
          <option value="Exam2">Exam 2</option>
        </select>
      </div> */}

      <div>
        <label>Captcha *</label>
        <input
          type="text"
          name="captcha"
          value={formData.captcha}
          onChange={handleChange}
        />
        <img src="captcha-placeholder.png" alt="captcha" />
      </div>

      <label>
        <input
          type="checkbox"
          name="consent"
          value={formData.consent}
          onChange={handleChange}
        />{' '}
      </label>
      <h8> I authorize Ymca Institute to contact me via Email, SMS, WhatsApp, and Call.</h8>

      <button type="submit">Submit</button>
    </form>
      ) : (
        // Render the login form
        <form onSubmit={handleLoginSubmit} className="login-form">
          <h2>Login</h2>
          <div>
            <label>Email *</label>
            <input
              type="email"
              name="email"
              value={loginData.email}
              onChange={handleLoginChange}
            />
            <span className="error">{errors.email}</span>
          </div>

          <div>
            <label>Password *</label>
            <input
              type="password"
              name="password"
              value={loginData.password}
              onChange={handleLoginChange}
            />
            <span className="error">{errors.password}</span>
          </div>

          <button type="submit">Login</button>
        </form>
      )}
    </div>
  );
};

export default RegistrationForm;
