import React, { useState } from 'react';
import '../../cssss/Student_css/Your_Profile.css';

const ApplicationForm = () => {
    const [formData, setFormData] = useState({
        name: '',
        fatherName: '',
        motherName: '',
        address: '',
        contact: '',
        email: '',
        photo: null,
        signature: null,
        xMarks: '',
        xMarksheet: null,
        xiiMarks: '',
        xiiMarksheet: null,
        state: '',
        city: '',
        programType: '',
        programApplied: '',
        dob: '',
        nationality: '',
        gender: '',
        maritalStatus: '',
        religion: '',
        category: '',
        subCategory: '',
        caste: '',
        fatherOccupation: '',
        familyIncome: '',
        fatherContact: '',
        familyId: '',
    });

    const handleChange = (e) => {
        const { name, value, files } = e.target;
        setFormData({
            ...formData,
            [name]: files ? files[0] : value,
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log(formData);
    };

    return (
      <>
        {/* Basic Details Section */}
        <div className="dashboard-container">
            <h2>Basic Details</h2>
            <form onSubmit={handleSubmit}>
                <div className="form-fields-left">
                    <label htmlFor="name">Full Name:</label>
                    <input
                        type="text"
                        id="name"
                        name="name"
                        value={formData.name}
                        onChange={handleChange}
                        required
                    />
                    <label htmlFor="motherName">Mother's Name:</label>
                    <input
                        type="text"
                        id="motherName"
                        name="motherName"
                        value={formData.motherName}
                        onChange={handleChange}
                        required
                    />
                    </div>
                    <div className="form-fields-right">
                    <label htmlFor="fatherName">Father's Name:</label>
                    <input
                        type="text"
                        id="fatherName"
                        name="fatherName"
                        value={formData.fatherName}
                        onChange={handleChange}
                        required
                    />
                     <label htmlFor="dob">Date of Birth:</label>
                    <input
                        type="date"
                        id="dob"
                        name="dob"
                        value={formData.dob}
                        onChange={handleChange}
                        required
                    />
                    
                </div>
            </form>
        </div>

        {/* Educational Details Section */}
        {/* <div className="dashboard-container">
            <h2>Educational Details</h2>
            <form onSubmit={handleSubmit}>
                <div className="form-fields">
                    <label htmlFor="xMarks">Xth Marks:</label>
                    <input
                        type="text"
                        id="xMarks"
                        name="xMarks"
                        value={formData.xMarks}
                        onChange={handleChange}
                        required
                    />
                    <label htmlFor="xMarksheet">Upload Xth Marksheet:</label>
                    <input
                        type="file"
                        id="xMarksheet"
                        name="xMarksheet"
                        accept="image/*"
                        onChange={handleChange}
                        required
                    />

                    <label htmlFor="xiiMarks">XII Marks:</label>
                    <input
                        type="text"
                        id="xiiMarks"
                        name="xiiMarks"
                        value={formData.xiiMarks}
                        onChange={handleChange}
                        required
                    />
                    <label htmlFor="xiiMarksheet">Upload XII Marksheet:</label>
                    <input
                        type="file"
                        id="xiiMarksheet"
                        name="xiiMarksheet"
                        accept="image/*"
                        onChange={handleChange}
                        required
                    />
                </div>
            </form>
        </div> */}
      </>
    );
};

export default ApplicationForm;

