import React, { useState } from 'react';

export default function EducationalDetails() {
    const [formData, setFormData] = useState({
        xIIth: '',
        xth: '',
        graduation: ''
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prevData => ({
            ...prevData,
            [name]: value
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        // Submit form logic here
        console.log('Educational Details submitted:', formData);
    };

    return (
        <div className="dashboard-container">
            <h2>Educational Details</h2>
            <h6>Secondary :</h6>
            <h6>Senior Secondary:</h6>
            <h6>Graduation:</h6><h7>(if applicable)</h7>
            <form onSubmit={handleSubmit}>
                <div className="form-fields-left">
                    <label htmlFor="xIIth">12th:</label>
                    <input
                        type="text"
                        id="xIIth"
                        name="xIIth"
                        value={formData.xIIth}
                        onChange={handleChange}
                        required
                    />
                    <label htmlFor="xth">10th:</label>
                    <input
                        type="text"
                        id="xth"
                        name="xth"
                        value={formData.xth}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div className="form-fields-right">
                    <label htmlFor="graduation">Graduation:</label>
                    <input
                        type="text"
                        id="graduation"
                        name="graduation"
                        value={formData.graduation}
                        onChange={handleChange}
                        required
                    />
                </div>
            </form>
        </div>
    );
}
