import React from 'react';
import axios from 'axios';

export default function Your_application_form({ userId }) {
  const handlePreviewClick = async () => {
    try {
      const response = await axios.get(`http://localhost:3000/api/PDF/${userId}`, {
        responseType: 'blob', // Important to set the response type to blob
      });
  
      if (response.status === 200) {
        const blob = new Blob([response.data], { type: 'application/pdf' });
        const url = window.URL.createObjectURL(blob);
        window.open(url, '_blank'); // Open the PDF in a new tab
      } else {
        console.error('Failed to fetch profile data');
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };
  

  return (
    <div>
      <p>Here you can preview your application and download it in pdf form</p>
      
      <div style={{ display: 'flex', gap: '10px', justifyContent: 'center' }}>
        <button onClick={handlePreviewClick} style={{
          backgroundColor: '#f44336',
          color: 'white',
          padding: '10px 20px',
          border: 'none',
          borderRadius: '5px',
          cursor: 'pointer',
          width: '118px',
        }}>
          Preview
        </button>
        
        <button style={{
          backgroundColor: '#2196F3',
          color: 'white',
          padding: '10px 20px',
          border: 'none',
          borderRadius: '5px',
          cursor: 'pointer',
          width: '118px',
        }}>
          Payment
        </button>
      </div>
    </div>
  );
}
