import React from 'react';
import axios from 'axios';

export default function Your_application_form({ userId }) {
  const handlePreviewClick = async () => {
    try {
      const response = await axios.get(`http://localhost:3000/api/PDF/${userId}`, {
        headers: {
          'Content-Type': 'application/json',
        },
      });

      if (response.status === 200) {
        const profileData = response.data;
        // Create a new tab and display profile data
        const newTab = window.open('', '_blank');
        newTab.document.write(`<html><head><title>Profile Data</title></head><body>`);
        newTab.document.write(`<h1>Profile Data</h1>`);
        newTab.document.write(`<p><strong>Name:</strong> ${profileData.name}</p>`);
        newTab.document.write(`<p><strong>Email:</strong> ${profileData.email}</p>`);
        newTab.document.write(`<p><strong>Age:</strong> ${profileData.age}</p>`);
        newTab.document.write(`<p><strong>Address:</strong> ${profileData.address}</p>`);
        newTab.document.write(`</body></html>`);
        newTab.document.close(); // Close the document to render it
      } else {
        console.error('Failed to fetch profile data');
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div>
      <p>Here you can preview your application and download it in pdf form</p>
      
      <div style={{ display: 'flex', gap: '10px', justifyContent: 'center' }}>
        <button onClick={handlePreviewClick} style={{
          backgroundColor: '#f44336',
          color: 'white',
          padding: '10px 20px',
          border: 'none',
          borderRadius: '5px',
          cursor: 'pointer',
          width: '118px',
        }}>
          Preview
        </button>
        
        <button style={{
          backgroundColor: '#2196F3',
          color: 'white',
          padding: '10px 20px',
          border: 'none',
          borderRadius: '5px',
          cursor: 'pointer',
          width: '118px',
        }}>
          Payment
        </button>
      </div>
    </div>
  );
}
