import React, { useState } from 'react';

export default function EducationalDetails() {
    const [formData, setFormData] = useState({
        xIIth: '',
        xth: '',
        graduation: ''
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prevData => ({
            ...prevData,
            [name]: value
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        // Submit form logic here
        console.log('Educational Details submitted:', formData);
    };

    return (
        <div className="dashboard-container">
            <h2>Educational Details</h2>
            <h6>Secondary :</h6>
            <label htmlFor="boardx">Board:</label>
            <select
                        name="boardx"
                        value={formData.boardx}
                        onChange={handleChange}
                        required
                    >
                        <option value="">Select Board</option>
                        <option value="Hbse">Board of School Education Haryana</option>
                        <option value="Cbse">Central Board of School Education</option>
                    </select>
            <h6>Senior Secondary:</h6>
            <h6>Graduation:</h6><h7>(if applicable)</h7>
            <form onSubmit={handleSubmit}>
                <div className="form-fields-left">
                    
                </div>
                <div className="form-fields-right">
                    <label htmlFor="graduation">Graduation:</label>
                    <input
                        type="text"
                        id="graduation"
                        name="graduation"
                        value={formData.graduation}
                        onChange={handleChange}
                        required
                    />
                </div>
            </form>
        </div>
    );
}
