import React, { useEffect } from 'react';
import axios from 'axios';

export default function Your_application_form() {

  const handlePreviewClick = async () => {

    const token = localStorage.getItem('token'); 
    console.log('Retrieved token:', token);// Get the token from local storage
    if (!token) {
      console.error('No access token found.');
      return; // Early return if no token
  }

    try {
      const response = await axios.get('http://localhost:3000/api/PDF/genpdf',
       {
        headers: {
          'Authorization': `Bearer ${token}`, // Include the token in the headers
          'Content-Type': 'application/json',
        },
        responseType: 'blob', // Ensure the response is treated as a blob for PDF
      });
      console.log("API response:", response.data);

      if (response.status === 200) {
        const url = window.URL.createObjectURL(new Blob([response.data]));
        const link = document.createElement('a');
        link.href = url;
        link.setAttribute('download', 'profile.pdf'); // Set the filename for the PDF
        document.body.appendChild(link);
        link.click();
        link.remove(); // Clean up
      } else {
        console.error('Failed to fetch profile data');
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div>
      <p>Here you can preview your application and download it in pdf form</p>
      
      <div style={{ display: 'flex', gap: '10px', justifyContent: 'center' }}>
        <button onClick={handlePreviewClick} style={{
          backgroundColor: '#f44336',
          color: 'white',
          padding: '10px 20px',
          border: 'none',
          borderRadius: '5px',
          cursor: 'pointer',
          width: '118px',
        }}>
          Preview
        </button>
        
        <button style={{
          backgroundColor: '#2196F3',
          color: 'white',
          padding: '10px 20px',
          border: 'none',
          borderRadius: '5px',
          cursor: 'pointer',
          width: '118px',
        }}>
          Payment
        </button>
      </div>
    </div>
  );
}
