import React,{useState,useEffect} from 'react'

export default function Basic_Details({ onDataChange }) {
    

    const [formData, setFormData] = useState({
        name: '',
        fatherName: '',
        motherName: '',
        session:'',
        mobileNo: '',
        email: '',
        aadharNo:'',
        familyId:'',
        programType: '',
        programApplied: '',
        dob: '',
        nationality: '',
        gender: '',
        maritalStatus: '',
        religion: '',
        category: '',
        subCategory: '',
        caste: '',
        fatherOccupation: '',
        familyIncome: '',
        fatherContact: '',
    });
    
    const handleChange = (e) => {
        const { name, value, files } = e.target;
        setFormData({
            ...formData,
            [name]: files ? files[0] : value,
        });
        // console.log('Sending data from Basic_Details to parent:', formData);
        // onDataChange(formData);
        
    };
    
    // const handleSubmit = (e) => {
        //     e.preventDefault();
        //     console.log(formData);
        // };
    const [debouncedFormData, setDebouncedFormData] = useState(formData);
    useEffect(() => {
        const timerId = setTimeout(() => {
            setDebouncedFormData(formData);
        }, 200); // Delay of 500ms (you can adjust this delay)

        // Cleanup timer on component unmount or when formData changes
        return () => {
            clearTimeout(timerId);
        };
    }, [formData]);

    // Only send the debounced form data to the parent
    useEffect(() => {
        console.log('Sending debounced data from Basic_Details to parent:', debouncedFormData);
        onDataChange(debouncedFormData);
    }, [debouncedFormData, onDataChange]);

  return (
    <>
     <div className="dashboard-container">
            <h2>Basic Details</h2>
            <form>
                <div className="form-fields-left">
                    <label htmlFor="name">Full Name:</label>
                    <input
                        type="text"
                        id="name"
                        name="name"
                        value={formData.name}
                        onChange={handleChange}
                        required
                    />
                    <label htmlFor="motherName">Mother's Name:</label>
                    <input
                        type="text"
                        id="motherName"
                        name="motherName"
                        value={formData.motherName}
                        onChange={handleChange}
                        required
                    />
                    <label htmlFor="gender">Gender:</label>
                    <select
                        name="gender"
                        value={formData.gender}
                        onChange={handleChange}
                        required
                    >
                        <option value="">Select Gender</option>
                        <option value="Male">Male</option>
                        <option value="Female">Female</option>
                        <option value="Other">Other</option>
                    </select>

                    <label htmlFor="nationality">Nationality:</label>
                    <select
                        name="nationality"
                        value={formData.nationality}
                        onChange={handleChange}
                        required
                    >
                        <option value="">Select Nationality</option>
                        <option value="indian">Indian</option>
                    </select>

                     <label htmlFor="maritalStatus">Marital Status:</label>
                    <select
                        name="maritalStatus"
                        value={formData.maritalStatus}
                        onChange={handleChange}
                        required
                    >
                        <option value="">Select Marital Status</option>
                        <option value="Single">Single</option>
                        <option value="Married">Married</option>
                    </select>
                    <label htmlFor="religion">Religion:</label>
                    <input
                        type="text"
                        id="religion"
                        name="religion"
                        value={formData.religion}
                        onChange={handleChange}
                        required
                    />
                    <label htmlFor="caste">Caste:</label>
                    <input
                        type="text"
                        id="caste"
                        name="caste"
                        value={formData.caste}
                        onChange={handleChange}
                    />
                    <label htmlFor="aadharNo">Aadhar No</label>
                    <input
                        type="text"
                        id="aadharNo"
                        name="aadharNo"
                        value={formData.aadharNo}
                        onChange={handleChange}
                    />
                     <label htmlFor="fatherContact">Father's mobileNo</label>
                    <input
                        type="text"
                        id="fatherContact"
                        name="fatherContact"
                        value={formData.fatherContact}
                        onChange={handleChange}
                    />
                     <label htmlFor="fatherOccupation">Father's Occupation</label>
                     <select
                        name="fatherOccupation"
                        value={formData.fatherOccupation}
                        onChange={handleChange}
                        required
                    >
                        <option value="">Select Occupation</option>
                        <option value="GovtEmployee">Govt. Employee</option>
                        <option value="CorpEmployee">Corporate Employee</option>
                        <option value="Other">Other</option>
                    </select>
                    </div>
                    <div className="form-fields-right">
